  <script>
    // Smooth scroll for the navigation and links with .scrollto classes
  $('.main-nav a, .mobile-nav a, .scrollto').on('click', function() {
    if (location.pathname.replace(/^\//, '') == this.pathname.replace(/^\//, '') && location.hostname == this.hostname) {
      var target = $(this.hash);
      if (target.length) {
        var top_space = 0;

        if ($('#header').length) {
          top_space = $('#header').outerHeight();

          if (! $('#header').hasClass('header-scrolled')) {
            top_space = top_space - 20;
          }
        }

        $('html, body').animate({
          scrollTop: target.offset().top - top_space
        }, 1500, 'easeInOutExpo');

        if ($(this).parents('.main-nav, .mobile-nav').length) {
          $('.main-nav .active, .mobile-nav .active').removeClass('active');
          $(this).closest('li').addClass('active');
        }

        if ($('body').hasClass('mobile-nav-active')) {
          $('body').removeClass('mobile-nav-active');
          $('.mobile-nav-toggle i').toggleClass('fa-times fa-bars');
          $('.mobile-nav-overly').fadeOut();
        }
        return false;
      }
    }
  });

  // Navigation active state on scroll
  var nav_sections = $('section');
  var main_nav = $('.main-nav, .mobile-nav');
  var main_nav_height = $('#header').outerHeight();

  $(window).on('scroll', function () {
    var cur_pos = $(this).scrollTop();
  
    nav_sections.each(function() {
      var top = $(this).offset().top - main_nav_height,
          bottom = top + $(this).outerHeight();
  
      if (cur_pos >= top && cur_pos <= bottom) {
        main_nav.find('li').removeClass('active');
        main_nav.find('a[href="#'+$(this).attr('id')+'"]').parent('li').addClass('active');
      }
    });
  });   
 
    // focus sidebar form

    if($(window).width() < 767){
      $('a[href^="#sidebar_focus"]').click(function() { 
        $('html,body').animate({ scrollTop: $(this.hash).offset().top}, 1200);         
        return false; 
        e.preventDefault(); 
      }); 
  }

    // focus form
    if($(window).width() > 767){
          $('body').on('click','.similar_itineraries_focus', function(){
          $( ".page_block" ).fadeIn();
          $(".sidebar").css('z-index', '9999');                                      
      });
         $( ".page_block" ).click(function() {
         $( ".page_block" ).hide();  
         $(".sidebar").css('z-index', '98');
      });  
    }

    // show mobile sticky button
    $(window).scroll(function(){
      if ($(window).scrollTop() >= 560){
          $('.mobile_sticky').addClass('show_sticky');
      } else {
        $('.mobile_sticky').removeClass('show_sticky');
      }
    });
      function ajaxindicatorstop() {
    $('#resultLoading .bg').height('100%');
    $('#resultLoading').fadeOut(500);
    $('body').css('cursor', 'default');
}

function ajaxindicatorstart(text,url) {
    
    if ($('body').find('#resultLoading').attr('id') != 'resultLoading') {
        $('body').append('<div id="resultLoading" style="display:none"><div><img src="'+url+'assets/images/loader.png"><div id="divtext">' + text + '</div></div><div class="bg"></div></div>');
    }
    else {
        $("#divtext").html(text)
    }

    $('#resultLoading').css({
        'width': '100%',
        'height': '100%',
        'position': 'fixed',
        'z-index': '10000000',
        'top': '0',
        'left': '0',
        'right': '0',
        'bottom': '0',
        'margin': 'auto'
    });
    
    $('#resultLoading .bg').css({
        'background': '#000000',
        'opacity': '0.7',
        'width': '100%',
        'height': '100%',
        'position': 'absolute',
        'top': '0'
    });

    $('#resultLoading>div:first').css({
        'width': '100%',
        'height': '75px',
        'text-align': 'center',
        'position': 'fixed',
        'top': '0',
        'left': '0',
        'right': '0',
        'bottom': '0',
        'margin': 'auto',
        'font-size': '16px',
        'z-index': '10',
        'color': '#ffffff'

    });

    $('#resultLoading .bg').height('100%');
    $('#resultLoading').fadeIn(1000);
    $('body').css('cursor', 'wait');
}
  // Append validation message function
    function appendMsgs(form,msg,errorClass){

	    $('.error').remove();
	    if(typeof(msg) == 'object'){
	      
	    $.each(msg,function(m,k){
	        $(form+' input[name="'+m+'"]').parent('div').append('<span id="'+m+'-error" class="error">'+k+'</span>');
	        $(form+' select[name="'+m+'"]').parent('div').append('<span id="'+m+'-error" class="error">'+k+'</span>');
	        $(form+' textarea[name="'+m+'"]').parent('div').append('<span id="'+m+'-error" class="error">'+k+'</span>');
	    });
	    }
	    else{
	      if(errorClass != ''){
	      $(form+' '+errorClass).html(msg);
	      }
	    }
    }
  </script>
